import win32api
import win32gui
import win32con
import win32ui
from PIL import Image, ImageTk
import tkinter as tk
import ctypes
import ctypes.wintypes
import base64
import cv2
import numpy
import time

MOUSE_LEFTDOWN_LEFTUP_DISTANCE = 0.015
GAME_X = 0
GAME_Y = 0
CARD_GAME_LEFT = 298
CARD_GAME_TOP = 222
CARD_GAME_RIGHT = 732
CARD_GAME_BOTTOM = 580
CARD_WIDTH = 0
CARD_HEIGHT = 0

GAME_SCREENSHOT = None
NUMBER_OF_CARDS = None
CARD_X_LOCATIONS = None
CARD_Y_LOCATIONS = None

def capture_window(window_title):
    global GAME_X, GAME_Y
    hwnd = win32gui.FindWindow(None, window_title)
    if hwnd == 0:
        return None

    # Get window dimensions
    rect = win32gui.GetWindowRect(hwnd)
    left, top, right, bottom = rect
    width = right - left
    height = bottom - top

    GAME_X = left
    GAME_Y = top

    # Create a device context
    hwnd_dc = win32gui.GetWindowDC(hwnd)
    mfc_dc = win32ui.CreateDCFromHandle(hwnd_dc)
    save_dc = mfc_dc.CreateCompatibleDC()

    # Create a bitmap compatible with the window's DC
    save_bitmap = win32ui.CreateBitmap()
    save_bitmap.CreateCompatibleBitmap(mfc_dc, width, height)
    save_dc.SelectObject(save_bitmap)

    # Copy the screen into our device context
    save_dc.BitBlt((0, 0), (width, height), mfc_dc, (0, 0), win32con.SRCCOPY)

    # Convert the bitmap to PIL image
    bmp_info = save_bitmap.GetInfo()
    bmp_buffer = save_bitmap.GetBitmapBits(True)
    img = Image.frombuffer(
        'RGB',
        (bmp_info['bmWidth'], bmp_info['bmHeight']),
        bmp_buffer,
        'raw',
        'BGRX',
        0,
        1
    )

    # Clean up
    save_dc.DeleteDC()
    mfc_dc.DeleteDC()
    win32gui.ReleaseDC(hwnd, hwnd_dc)

    return img

def display_image(img):
    # Update existing label with the new image
    img_tk = ImageTk.PhotoImage(img)
    label.config(image=img_tk)
    label.img = img_tk
    root.update()

def capture_and_display(window_title):
    global GAME_SCREENSHOT
    screenshot = capture_window(window_title)
    if screenshot:
        # Crop the image (left, top, right, bottom) before displaying
        cropped_image = screenshot.crop((CARD_GAME_LEFT, CARD_GAME_TOP, CARD_GAME_RIGHT, CARD_GAME_BOTTOM))
        GAME_SCREENSHOT = cropped_image
        display_image(cropped_image)
    else:
        print("Window not found or could not capture screenshot.")

def capture_and_update():
    window_title = "Gersang"  # Replace with the title of the window you want to capture
    capture_and_display(window_title)

def read_image_from_b64(base64_string):
  image_data = base64.b64decode(base64_string)
  np_array = numpy.frombuffer(image_data, numpy.uint8)
  return cv2.imdecode(np_array, 0)

def mouse_left_click(x,y):
  win32api.SetCursorPos((x,y))
  win32api.mouse_event(win32con.MOUSEEVENTF_LEFTDOWN,x,y,0,0)
  time.sleep(MOUSE_LEFTDOWN_LEFTUP_DISTANCE)
  win32api.mouse_event(win32con.MOUSEEVENTF_LEFTUP,x,y,0,0)

def click_cards_coordination(card_width, card_height, cards_coordination):
    for i, pt in enumerate(cards_coordination):
        card_x = CARD_GAME_LEFT + pt[0]
        card_y = CARD_GAME_TOP + pt[1]
        click_x = GAME_X + card_x + int(card_width / 2)
        click_y = GAME_Y + card_y + int(card_height / 2)
        mouse_left_click(click_x, click_y)
        time.sleep(0.30)
        win32api.SetCursorPos((50,50))
        time.sleep(0.30)
        update_card_game_card(card_x, card_y, card_width, card_height, pt[0], pt[1])
        time.sleep(0.70)

def update_card_game_card(card_x, card_y, card_width, card_height, card_org_x, card_org_y):
    global GAME_SCREENSHOT
    screenshot = capture_window('Gersang')
    if screenshot:
        # Crop the image (left, top, right, bottom) before displaying
        cropped_image = screenshot.crop((card_x, card_y, card_x + card_width, card_y + card_height))
        GAME_SCREENSHOT.paste(cropped_image, (card_org_x, card_org_y))
        display_image(GAME_SCREENSHOT)
    else:
        print("Window not found or could not capture screenshot.")

def start_click_cards():
    global NUMBER_OF_CARDS
    global CARD_X_LOCATIONS
    global CARD_Y_LOCATIONS
    global CARD_WIDTH
    global CARD_HEIGHT
    NUMBER_OF_CARDS = 0
    CARD_X_LOCATIONS = None
    CARD_Y_LOCATIONS = None
    card_width, card_height, cards_coordination = find_cards_coordination()
    CARD_WIDTH = card_width
    CARD_HEIGHT = card_height
    click_cards_coordination(card_width, card_height, cards_coordination)

def find_cards_coordination():
    global GAME_SCREENSHOT
    global NUMBER_OF_CARDS
    global CARD_X_LOCATIONS
    global CARD_Y_LOCATIONS
    card_base64 = ''
    img_gray = cv2.cvtColor(numpy.asarray(GAME_SCREENSHOT), cv2.COLOR_BGR2GRAY)
    card_template = read_image_from_b64(card_base64)
    card_width, card_height = card_template.shape[::-1]
    result = cv2.matchTemplate(img_gray,card_template,cv2.TM_CCOEFF_NORMED)
    threshold = 0.8
    location = numpy.where(result>=threshold)
    x_locations, y_locations = location[::-1]
    CARD_X_LOCATIONS = list(set(x_locations))
    CARD_Y_LOCATIONS = list(set(y_locations))
    cards_coordination = list(zip(*location[::-1]))
    NUMBER_OF_CARDS = len(cards_coordination)
    return card_width, card_height, cards_coordination

def closest(lst, K):
    lst = numpy.asarray(lst)
    idx = (numpy.abs(lst - K)).argmin()
    return lst[idx]

def click_pairs():
    global NUMBER_OF_CARDS
    global GAME_SCREENSHOT
    global CARD_X_LOCATIONS
    global CARD_Y_LOCATIONS
    global CARD_WIDTH
    global CARD_HEIGHT
    global GAME_X
    global GAME_Y
    matches = [
        '',
        '',
        '',
        '',
        '',
        '',
        '',
        '',
        '',
        '',
        '',
        '',
        '',
        '',
        '',
        '',
        '',
        '',
        '',
        ''
    ]
    pairs_set = set()
    pairs = []

    for x in range(int(NUMBER_OF_CARDS / 2)):
        match_card_template = read_image_from_b64(matches[x])
        img_gray = cv2.cvtColor(numpy.asarray(GAME_SCREENSHOT), cv2.COLOR_BGR2GRAY)
        res = cv2.matchTemplate(img_gray, match_card_template, cv2.TM_CCOEFF_NORMED)
        threshold = 0.8
        loc = numpy.where(res >= threshold)
        for pt in zip(*loc[::-1]):
            coor = (closest(CARD_X_LOCATIONS, pt[0]), closest(CARD_Y_LOCATIONS, pt[1]))
            if not(coor in pairs):
                pairs_set.add(coor)
                pairs.append(coor)

    for x in pairs:
        card_x = CARD_GAME_LEFT + x[0]
        card_y = CARD_GAME_TOP + x[1]
        click_x = GAME_X + card_x + int(CARD_WIDTH / 2)
        click_y = GAME_Y + card_y + int(CARD_HEIGHT / 2)
        mouse_left_click(click_x, click_y)
        time.sleep(0.4)

def start():
    capture_and_update()
    start_click_cards()
    click_pairs()

root = tk.Tk()
root.title("Capture Screenshot")

